import {
	FileExcelOutlined,
	FilePdfOutlined,
	FileTextOutlined,
	FileUnknownOutlined,
	FileWordOutlined,
	FileZipOutlined,
} from '@ant-design/icons';
import {Image} from 'antd';
import React from 'react';
import './MediaUtils.less';
import {classic} from 'rt-design';

const {Title, Text} = classic;

/** определяет изображение по расширению файла*/
export const isImage = (extension) => {
	switch (extension) {
		case 'jpg':
		case 'png':
		case 'gif':
			return true;
		default:
			return false;
	}
};

/** отображает иконку по расширению файла */
export const CommonFileIcon = (extension) => {
	switch (extension) {
		case 'pdf':
			return <FilePdfOutlined />;
		case 'txt':
			return <FileTextOutlined />;
		case 'xls':
		case 'xlsx':
			return <FileExcelOutlined />;
		case 'doc':
		case 'docx':
			return <FileWordOutlined />;
		case 'zip':
		case '7z':
		case 'rar':
			return <FileZipOutlined />;
		default:
			return <FileUnknownOutlined />;
	}
};

/**
 * превью вложенных файлов. {items} .fileUrl, .fileType, .fileName,
 * */
export const AttachmentsPreview = ({items}) => {
	const images = [];
	const files = [];

	const DisplayImages = ({images}) => {
		return (
			<>
				<Title level={5}>Изображения</Title>
				<div className={'imagesPreviewList'}>
					<Image.PreviewGroup>
						{images.map((image, index) => (
							<Image
								key={index}
								width={150}
								height={150}
								src={image.fileUrl}
								fallback='data:image/png;base64,'
							/>
						))}
					</Image.PreviewGroup>
				</div>
			</>
		);
	};

	const DisplayFiles = ({files}) => {
		return (
			<>
				<Title level={5}>Файлы</Title>
				<ul className={'filesPreviewList'}>
					{files.map((file, index) => (
						<li key={index}>
							<a
								href={file.fileUrl}
								target={'_blank'}
								rel={'noreferrer'}
							>
								<CommonFileIcon extension={file.fileType} />
								<span>{file.fileName}</span>
							</a>
						</li>
					))}
				</ul>
			</>
		);
	};

	if (items && items.length) {
		items.forEach((item) => {
			if (isImage(item.fileType)) images.push(item);
			else files.push(item);
		});

		return (
			<>
				{images.length ? <DisplayImages images={images} /> : null}
				{files.length ? <DisplayFiles files={files} /> : null}
			</>
		);
	} else return <Text>Нет загруженных файлов</Text>;
};
